import boxen from "boxen"

const defaultConfig = {
  padding: 1,
  borderColor: `blue`,
  borderStyle: `double`,
} as boxen.Options

const defaultMessage =
  `Erxes collects anonymous usage analytics\n` +
  `to help improve Erxes for all users.\n`

/**
 * Analytics notice for the end-user
 */
export function showAnalyticsNotification(
  config: boxen.Options = defaultConfig,
  message: string = defaultMessage
): void {
  console.log(boxen(message, config))
}
