import ActionButtons from "erxes-common-ui/lib/components/actionButtons";
import Button from "erxes-common-ui/lib/components/button";
import Icon from "erxes-common-ui/lib/components/icon";
import ModalTrigger from "modules/common/components/ModalTrigger";
import Tip from "modules/common/components/Tip";
import { __ } from "modules/common/utils";
import React from "react";

type Props = {
  size?: "sm" | "lg" | "xl";
  object: any;
  renderForm: (doc: {
    object: any;
    closeModal: () => void;
    save: () => void;
  }) => void;
  additionalActions?: (object: any) => void;
  remove?: (id: string) => void;
  save: () => void;
};

export default class RowActions extends React.Component<Props, {}> {
  remove = () => {
    const { remove } = this.props;

    if (remove) {
      remove(this.props.object._id);
    }
  };

  renderRemoveAction = () => {
    if (!this.props.remove) {
      return null;
    }

    return (
      <Tip text={__("Delete")} placement="top">
        <Button btnStyle="link" onClick={this.remove} icon="times-circle" />
      </Tip>
    );
  };

  renderEditAction = () => {
    const { size, renderForm, object, save } = this.props;

    const editTrigger = (
      <Button btnStyle="link">
        <Tip text={__("Edit")} placement="top">
          <Icon icon="edit-3" />
        </Tip>
      </Button>
    );

    const content = (props) => {
      return renderForm({ ...props, object, save });
    };

    return (
      <ModalTrigger
        size={size}
        title="Edit"
        enforceFocus={false}
        trigger={editTrigger}
        content={content}
      />
    );
  };

  render() {
    const { additionalActions, object } = this.props;

    return (
      <td>
        <ActionButtons>
          {this.renderEditAction()}
          {this.renderRemoveAction()}
          {additionalActions && additionalActions(object)}
        </ActionButtons>
      </td>
    );
  }
}
