const commonParamsDef = `
  $title: String,
  $description: String,
  $buttonText: String,
  $type: String!
`;

const commonParams = `
  title: $title,
  description: $description,
  buttonText: $buttonText,
  type: $type
`;

const addForm = `
  mutation formsAdd(${commonParamsDef}) {
    formsAdd(${commonParams}) {
      _id
    }
  }
`;

const editForm = `
  mutation formsEdit($_id: String!, ${commonParamsDef}) {
    formsEdit(_id: $_id, ${commonParams}) {
      _id
    }
  }
`;

const commonFieldParamsDef = `
  $type: String,
  $validation: String,
  $text: String,
  $description: String,
  $options: [String],
  $isRequired: Boolean,
  $order: Int
`;

const commonFieldParams = `
  type: $type,
  validation: $validation,
  text: $text,
  description: $description,
  options: $options,
  isRequired: $isRequired,
  order: $order
`;

const fieldsAdd = `
  mutation fieldsAdd(
    $contentType: String!,
    $contentTypeId: String,
    ${commonFieldParamsDef}
  ) {
      fieldsAdd(
        contentType: $contentType,
        contentTypeId: $contentTypeId,
        ${commonFieldParams}
      ) {
        _id
        contentTypeId
      }
  }
`;

const fieldsEdit = `
  mutation fieldsEdit($_id: String!, ${commonFieldParamsDef}) {
    fieldsEdit(_id: $_id, ${commonFieldParams}) {
      _id
      contentTypeId
    }
  }
`;

const fieldsRemove = `
  mutation fieldsRemove($_id: String!) {
    fieldsRemove(_id: $_id) {
      _id
    }
  }
`;

const commonFormSubmissionParamsDef = `
  $formId: String,
  $contentType: String,
  $contentTypeId: String,
  $formSubmissions: JSON,
`;

const commonFormSubmissionParams = `
  formId: $formId,
  contentType: $contentType,
  contentTypeId: $contentTypeId,
  formSubmissions: $formSubmissions
`;

const formSubmissionsSave = `
  mutation formSubmissionsSave(${commonFormSubmissionParamsDef}) {
    formSubmissionsSave(${commonFormSubmissionParams})
  }
`;

export default {
  addForm,
  editForm,
  fieldsAdd,
  fieldsEdit,
  fieldsRemove,
  formSubmissionsSave
};
