import Button from 'modules/common/components/Button';
import Icon from 'erxes-common-ui/lib/components/icon';
import ModalTrigger from 'modules/common/components/ModalTrigger';
import Tags from 'modules/common/components/Tags';
import Tip from 'modules/common/components/Tip';
import ActionButtons from 'erxes-common-ui/lib/components/actionButtons';
import { IButtonMutateProps } from 'modules/common/types';
import { __ } from 'modules/common/utils';
import { ITag } from 'modules/tags/types';
import React from 'react';
import Form from './Form';

type Props = {
  tag: ITag;
  type: string;
  count?: number;
  renderButton: (props: IButtonMutateProps) => JSX.Element;
  remove: (tag: ITag) => void;
};

function Row({ tag, type, count, renderButton, remove }: Props) {
  function removeTag() {
    remove(tag);
  }

  const editTrigger = (
    <Button btnStyle="link">
      <Tip text={__('Edit')} placement="top">
        <Icon icon="edit-3" />
      </Tip>
    </Button>
  );

  const content = props => (
    <Form {...props} type={type} tag={tag} renderButton={renderButton} />
  );

  return (
    <tr>
      <td>
        <Tags tags={[tag]} size="medium" />
      </td>
      <td>{count || '0'}</td>
      <td>
        <ActionButtons>
          <ModalTrigger
            title="Edit tag"
            trigger={editTrigger}
            content={content}
          />

          <Tip text={__('Delete')} placement="top">
            <Button btnStyle="link" onClick={removeTag} icon="times-circle" />
          </Tip>
        </ActionButtons>
      </td>
    </tr>
  );
}

export default Row;
